mapper.select:
SELECT COUNT(*) 
FROM replyjsp
WHERE bo_no = #{searchDetail.bo_no}

SELECT A.*
FROM
(
	SELECT ROWNUM rn, A.*
	FROM
	(
		SELECT rep_no, bo_no, rep_content, rep_writer, TO_CHAR(rep_date, 'YYYY-MM-DD') rep_date, rep_ip, rep_pass
		FROM replyjsp
		WHERE bo_no = #{searchDetail.bo_no}
		ORDER BY rep_date DESC
	) A 
) A
WHERE rn BETWEEN #{startRow} AND #{endRow}
